mapper.select:
SELECT

FROM sys_notice a


      AND a.type = #{type}


        AND a.tittle = #{tittle}


        AND a.creator = #{creator}


        AND a.to_user = #{toUser}





        ORDER BY ${orderBy}


        ORDER BY a.update_time DESC

mapper.update:
UPDATE sys_notice


        tittle = #{tittle}


        type = #{type}


        creator = #{creator}


        to_user = #{toUser}


        version = #{version}



    id = #{id}
