mapper.select:
SELECT

FROM sys_role a


      AND a.pid = #{pid}




        ORDER BY ${orderBy}


        ORDER BY a.update_time DESC

mapper.update:
UPDATE sys_user


        sort = #{sort}


        name = #{name}


        pid = #{pid}


        dept_id = #{deptId}


        tips = #{tips}


        update_time = #{updateTime}



    id = #{id}
