SELECT
FROM sys_media_gallery a
a.delflag = 0 AND a.id = #{id}

SELECT

FROM sys_media_gallery a

    a.delflag = 0

        AND a.media_type = #{mediaType}


        AND a.origin_name = #{originName}


        AND a.name = #{name}


        AND a.url = #{url}


        AND a.cover = #{cover}


        AND a.media_format = #{mediaFormat}


        AND a.size = #{size}


LIMIT 1

SELECT

FROM sys_media_gallery a

    a.delflag = 0

        AND a.media_type = #{mediaType}


        AND a.origin_name = #{originName}


        AND a.name = #{name}


        AND a.url = #{url}


        AND a.cover = #{cover}


        AND a.media_format = #{mediaFormat}


        AND a.size = #{size}




        ORDER BY ${orderBy}


        ORDER BY a.update_time DESC
