a.delflag = 0 AND a.id = #{id}

SELECT

FROM sys_operation_log a

    a.delflag = 0

        AND a.log_name = #{logName}


        AND a.user_id = #{userId}


        AND a.succeed = #{succeed}


LIMIT 1

SELECT

FROM sys_operation_log a

    a.delflag = 0

        AND a.log_name = #{logName}


        AND a.user_id = #{userId}


        AND a.succeed = #{succeed}




        ORDER BY ${orderBy}


